    fetchUserCategories()
  }, []) 


  let selectorOptions = categories.map( c => {
    return {
      label:c.name,
      value:c.name
    }
  })


  const onCreate = (value) => {
    console.log('create ', value)
    if (value != "") {
      console.log('create')
      createTransactionCategory(value)
    }
}

const defaultValue = () => {
  return selectorOptions.filter()
}

