import React, {useState} from 'react';
import { Modal} from 'antd';
import PopoverUI from './PopoverUI';

const ModalUI = ({reloadModal, reload}) => {

    const [isModalVisible, setIsModalVisible] = useState(false);
    const [selectedItem, setSelectedItem] = useState(null);

    const showModal = () => {
        setIsModalVisible(true);
      };

      const handleOk = () => {
        setIsModalVisible(false);
      };

      const handleCancel = () => {
        setIsModalVisible(false);
      };

    return(
        <Modal title={"Transaction details"} visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}>
            {selectedItem 
            ? <div className='modal'>
                <div className='row'>
                  <div>Transaction ID : </div>
                  <div>{selectedItem.transaction_details.uuid}</div>
                </div>
                <div className='row'>
                  <div>Vendor: </div>
                  <div>{selectedItem.transaction_details.vendor}</div>
                </div>
                <div className='row'>
                  <div>Category</div>
                  <PopoverUI 
                  closeModal={handleCancel} 
                  selected={selectedItem} 
                  reload={reload}
                  reloadModal={reloadModal}
                  > 
                  {selectedItem.transaction_details.category}
                  </PopoverUI>
                </div>
                <div className='row'>
                  <div>Total</div>
                  <div>{selectedItem.transaction_details.total}</div>
                </div>
              </div>
            : <div></div>
            }
          </Modal>
    )
}

export default ModalUI;
