import React, { useState, useEffect } from 'react';
import { Popover, Button } from 'antd';
import SelectUI from './SelectUI';
import CardUI from './CardUI';

const PopoverUI = ({children ,reload, selected}) => {

  const [categories, setCategories] = useState([])
  const [newCategory, setNewCategory] = useState(null)

  useEffect(() => {
    fetch('https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/categories')
      .then(response => response.json())
      .then(data => { setCategories(data) });

    // console.log(count)
  }, []) // if you add count, only run if count changes.

  const onSubmitTag = (value) => {
    fetch(`https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/transactions/${selected.transaction_details.id}`, {
      method: 'PUT',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body:JSON.stringify({"tag":value})
    })
    .then(response => response.json())
    .then(data => { reload() });
  }

  const onSelect = (value) => {
      console.log('value ', value)
      onSubmitTag(value)
  }

    const text = <span>Title</span>;
    const content = (
    <div>
       <CardUI title={"Edit Category"} onSelect={onSelect}>

       </CardUI>
    </div>
    );

    const buttonWidth = 70;

    return(
        <Popover placement="right" content={content} trigger="click">
            <a>{children}</a>
        </Popover>

    );
}

export default PopoverUI
