import React, { useState, useEffect } from 'react';
import { Button, Select } from 'antd';

const { Option } = Select;

const SelectUI = ({ onSelect, selectedItem, reload, resetSelect }) => {

  const [reloadTrick, setReloadTrick] = useState("");
  const [categories, setCategories] = useState([])
  const [create, setCreate] = useState(false)
  const [newCategory, setNewCategory] = useState(null)
  const [value, setValue] = useState(selectedItem && selectedItem.transaction_details.category)

  useEffect(() => {
    fetch('https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/categories')
      .then(response => response.json())
      .then(data => { setCategories(data) });

    // console.log(count)
  }, []) // if you add count, only run if count changes.


  const makeid = (length) => {
    var result           = '';
    var characters       = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789';
    var charactersLength = characters.length;
    for ( var i = 0; i < length; i++ ) {
      result += characters.charAt(Math.floor(Math.random() * 
      charactersLength));
   }
   return result;
}

const isCreate = (value) => {
  setCreate(value)
}

const onType = (value) => {
  setNewCategory(value)
}



  function onChange(value) {
    console.log(`selected ${value}`);
    onSelect()
  }

  function onBlur() {
    reload()
  }

  function onFocus() {

  }


  function onSearch(val) {
    const filteredCategories = categories.filter(category =>{
      return category.name.includes(val);
    })
    if(filteredCategories.length > 0){


    }
    else{
      // setOnCreate(true)
      isCreate(true)
      onType(val)
    }
  }

  function filterOption(input, option){
    const result = option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0 ;
    return result
  }

  const onCreate = () => {
    if (newCategory != null) {
        fetch(`https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/categories`, {
            method: 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify({ "category": newCategory })
        })
            .then(response => response.json())
            .then(data => { setCategories(data) });
    }
    console.log('click')
    setCreate(false);
}
const onSubmitTag = (value) => {
  setValue(value)
  console.log('VALUE VALUE ', value)
  console.log('SELECTED ITEM ', selectedItem)
  fetch(`https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/transactions/${selectedItem.transaction_details.uuid}`, {
    method: 'PUT',
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json'
    },
    body:JSON.stringify({"category":value})
  })
  .then(response => response.json())
  .then(data => { 
    reload() 
    resetSelect()
    // closeModal()
    // setVisible(false)
    // reloadModal(value);

  });
}

return !categories.length 
? <h2>Loading</h2>
:

  <div>
    <Select
        showSearch
        style={{ width: 200 }}
        placeholder="Select a category"
        optionFilterProp="children"
        onChange={onSubmitTag}
        onBlur={onBlur}
        onSearch={onSearch}
        filterOption={(filterOption)}
        allowClear
        value={value}
      >
        <Option value="all">All</Option>
        {
          categories.map(c => (
            <Option key={c.uuid} value={c.name}>{c.name}</Option>
          ))
        }
      </Select>
        {/* {onCreate ? <Button onClick={() => console.log('click')}>Create</Button> : null} */}
  </div>

}

export default SelectUI;
