import React, { useState, useEffect } from 'react';
import { Button, Select } from 'antd';

const { Option } = Select;

const SelectUI = ({ onSelect, isCreate, onCreate }) => {

  const [categories, setCategories] = useState([])
  const [newCategory, setNewCategory] = useState(null)

  useEffect(() => {
    fetch('https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/categories')
      .then(response => response.json())
      .then(data => { setCategories(data) });

    // console.log(count)
  }, [newCategory]) // if you add count, only run if count changes.

  function onChange(value) {
    console.log(`selected ${value}`);
    onSelect()
  }

  function onBlur() {
    // if(newCategory != null){
    //   console.log('NEW CATEGORY ', newCategory);
    //   fetch(`https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/categories`, {
    //     method: 'POST',
    //     headers: {
    //       'Accept': 'application/json',
    //       'Content-Type': 'application/json'
    //     },
    //     body:JSON.stringify({"category":newCategory})
    //   })
    //   .then(response => response.json())
    //   .then(data => { setCategories(data) });
    // }
  }

  function onFocus() {

  }


  function onSearch(val) {
    const filteredCategories = categories.filter(category =>{
      return category.name.includes(val);
    })
    if(filteredCategories.length > 0){

      // setOnCreate(false)
      isCreate(false)
    }
    else{
      // setOnCreate(true)
      isCreate(true)
      onCreate(val)
    }
  }

  function filterOption(input, option){
    const result = option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0 ;
    return result
  }

  return !categories.length ?
    <h1>Loading</h1> :
    <div>
      <Select
        showSearch
        style={{ width: 200 }}
        placeholder="Select a category"
        optionFilterProp="children"
        onChange={onSelect}
        onBlur={onBlur}
        onSearch={onSearch}
        filterOption={(filterOption)}
      >
        <Option value="all">All</Option>
        {
          categories.map(c => (
            <Option key={c.uuid} value={c.name}>{c.name}</Option>
          ))
        }
      </Select>
        {/* {onCreate ? <Button onClick={() => console.log('click')}>Create</Button> : null} */}
    </div>
}

export default SelectUI;
