import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { closeModal } from '../actions';
import { Modal, Select } from 'antd';
import PopoverUI from './PopoverUI';
import SelectUI from './SelectUI';

const { Option } = Select;

const ModalUI = ({ reload, visible, handleOk, handleCancel, selectedItem, modal, closeModal }) => {


  return (
    <Modal title={"Transaction details"} visible={modal} onOk={handleOk} onCancel={handleCancel}>
    {selectedItem
      ? <div className='modal'>
        <div className='row'>     
          <div>Transaction ID: </div>
          <div>{selectedItem.transaction_details.uuid}</div>
        </div>
        <div className='row'>
          <div>Vendor: </div>
          <div>{selectedItem.transaction_details.vendor}</div>
        </div>
        <div className='row'>
          <div>Category:</div>
          {/* <SelectUI/> */}
        </div>
        <div className='row'>
          <div>Total:</div>
          <div>{selectedItem.transaction_details.total}</div>
        </div>
      </div>
      : <div></div>
    }
  </Modal>
  )
}

const mapStateToProps = (state) => {
  return {
    modal: state.modal.open
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    closeModal: () => dispatch(closeModal())
  }
}
export default connect(mapStateToProps, mapDispatchToProps) (ModalUI);
