import React, { useState, useEffect } from 'react';
import { Button, Select } from 'antd';

const { Option } = Select;

const SelectUI = ({ onSelect }) => {

  const [categories, setCategories] = useState([])
  const [onCreate, setOnCreate] = useState(false)

  useEffect(() => {
    fetch('https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/categories')
      .then(response => response.json())
      .then(data => { setCategories(data) });

    // console.log(count)
  }, []) // if you add count, only run if count changes.

  function onChange(value) {
    console.log(`selected ${value}`);
    onSelect()
  }

  function onBlur() {
    console.log('blur');
  }

  function onFocus() {
    console.log('focus');
  }

  function onSearch(val) {


  }

  function filterOption(input, option){
    console.log('option ', option)
    console.log('input ', input)
    const result = option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0 ;
    if(result){
      return result
    }
  }

  return !categories.length ?
    <h1>Loading</h1> :
    <div>
      <Select
        showSearch
        style={{ width: 200 }}
        placeholder="Select a category"
        optionFilterProp="children"
        onChange={onSelect}
        onFocus={onFocus}
        onBlur={onBlur}
        onSearch={onSearch}
        filterOption={filterOption}
      >
        <Option value="all">All</Option>
        {
          categories.map(c => (
            <Option value={c.name}>{c.name}</Option>
          ))
        }
      </Select>
        {onCreate ? <Button>Create</Button> : null}
    </div>
}

export default SelectUI;
