const initialState = {
    categories: []
  }

export default (state = initialState, action) => {
    switch (action.type) {
      case 'FETCH_CATEGORIES':
        return {...state, categories: action.payload};
      case 'CREATE_CATEGORY':
        return {...state, categories: action.payload};
      default:
        return state;
    }
  };
