import React, { useState, useEffect } from 'react';
import { List, message, Avatar, Spin, Modal, Select} from 'antd';
import InfiniteScroll from 'react-infinite-scroller';
import EditableForm from './EditableForm';
import PopoverUI from './PopoverUI';
import ModalUI from './ModalUI';
import './list.css'

const { Option } = Select;

const fakeDataUrl = 'https://randomuser.me/api/?results=5&inc=name,gender,email,nat&noinfo';

const ListUI = ({data, reload}) => {

  const [visible, setVisible] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  const [category, setCategory] = useState("");
  const [reloadTrick, setReloadTrick] = useState("");
  const [categories, setCategories] = useState([])
  const [create, setCreate] = useState(false)
  const [newCategory, setNewCategory] = useState(null)
  const [value, setValue] = useState(selectedItem && selectedItem.transaction_details.category)

  useEffect(() => {

    // console.log(count)
  }, [selectedItem]) // if you add count, only run if count changes.

  useEffect(() => {
    console.log('use effect select ', value)
    fetch('https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/categories')
      .then(response => response.json())
      .then(data => { setCategories(data) });

    // console.log(count)
  }, [value]) // if you add count, only run if count changes.


  const showModal = () => {
    setVisible(true);
  };

  const handleOk = () => {
    setVisible(false);
    console.log('resect select')
  };

  const handleCancel = () => {
    setVisible(false);
  };

  const onTransactionClick = (item) => {
    console.log('ITEM ITEM ' , item)
    setSelectedItem(item);
    showModal()
  }

  const onFinish = (value, item) => {
    handleOk()
    onSubmitTag(value, item)
  };

  const reloadModal = () => {
   console.log('RELOAD MODAL LIST UI')
   handleOk()
  };

  const isCreate = (value) => {
    setCreate(value)
  }

  const onType = (value) => {
    setNewCategory(value)
  }





    function onBlur() {
      console.log('blur')
      reload()
    }

    function onFocus() {
      console.log('focus')
    }


    function onSearch(val) {
      const filteredCategories = categories.filter(category =>{
        return category.name.includes(val);
      })
      if(filteredCategories.length > 0){


      }
      else{
        // setOnCreate(true)
        isCreate(true)
        onType(val)
      }
    }

    function filterOption(input, option){
      const result = option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0 ;
      return result
    }

    const onCreate = () => {
      if (newCategory != null) {
          fetch(`https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/categories`, {
              method: 'POST',
              headers: {
                  'Accept': 'application/json',
                  'Content-Type': 'application/json'
              },
              body: JSON.stringify({ "category": newCategory })
          })
              .then(response => response.json())
              .then(data => { setCategories(data) });
      }
      console.log('click')
      setCreate(false);
  }
  const onSubmitTag = (value) => {
    setValue(value)
    console.log('VALUE VALUE ', value)
    console.log('SELECTED ITEM ', selectedItem)
    fetch(`https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/transactions/${selectedItem.transaction_details.uuid}`, {
      method: 'PUT',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      },
      body:JSON.stringify({"category":value})
    })
    .then(response => response.json())
    .then(data => { 
      reload() 
      handleOk()
      setValue('')
      // setVisible(false)
      // reloadModal(value);

    });
  }

    return ( 
      <div className="demo-infinite-container">
        <InfiniteScroll
          initialLoad={false}
          pageStart={0}
          useWindow={false}
          loadMore={() => console.log('load more')}
        >
              <Modal title={"Transaction details"} visible={visible} onOk={handleOk} onCancel={handleCancel}>
      {selectedItem
        ? <div className='modal'>
          <div className='row'>
            <div>Transaction ID : </div>
            <div>{selectedItem.transaction_details.uuid}</div>
          </div>
          <div className='row'>
            <div>Vendor: </div>
            <div>{selectedItem.transaction_details.vendor}</div>
          </div>
          <div className='row'>
            <div>Category</div>
            {/* <SelectUI closeModal={closeModal} resetSelect={resetSelect} reload={reload} selectedItem={selectedItem} /> */}
            <Select
              showSearch
              style={{ width: 200 }}
              placeholder="Select a category"
              optionFilterProp="children"
              onChange={onSubmitTag}
              onBlur={onBlur}
              onFocus={onFocus}
              onSearch={onSearch}
              filterOption={(filterOption)}
              allowClear
              value={value}
            >
              <Option value="all">All</Option>
              {
                categories.map(c => (
                  <Option key={c.uuid} value={c.name}>{c.name}</Option>
                ))
              }
            </Select>
          </div>
          <div className='row'>
            <div>Total</div>
            <div>{selectedItem.transaction_details.total}</div>
          </div>
        </div>
        : <div></div>
      }
    </Modal>
          <List
            dataSource={data}
            renderItem={item => (
              <div data-tag="test" onClick={() => onTransactionClick(item)}>
                <List.Item data={item} key={item.transaction_details.id}>
                <List.Item.Meta
                  title={<a><h2>{item.transaction_details.vendor}</h2></a>}

                />
                <List.Item.Meta
                  title={<a><h2>{item.transaction_details.category}</h2></a>}
                  description={item.transaction_details.tag}
                />
                <h2>{item.transaction_details.total} ₪</h2>


              </List.Item>
              </div>
            )}
          >
          </List>
        </InfiniteScroll>
      </div>
    );

}

export default ListUI ;
