import React, { useState, useEffect } from 'react';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
  } from "react-router-dom";
  { useHistory }
import { Layout, Menu, Breadcrumb } from 'antd';
import {
    DesktopOutlined,
    PieChartOutlined,
    FileOutlined,
    TeamOutlined,
    UserOutlined,
} from '@ant-design/icons';
import ListUI from '../components/ListUI';

const { Header, Content, Footer, Sider } = Layout;
const { SubMenu } = Menu;

const LayoutUI = ({children}) => {

    const [collapsed, setCollapsed] = useState(false)
    const [selectedKeys, setSelectedKeys] = useState(['transactions'])


    const onCollapse = collapsed => {
        console.log(collapsed);
        setCollapsed(!collapsed)
    };


    const linkTo = (item) => {
        setSelectedKeys((old) => [`${item.key}`])
        console.log('array ', selectedKeys)
        // window.location = `/${item.key}`
    }
    return (
        <Router>
            <Layout style={{ minHeight: '100vh' }}>
            <Sider collapsible collapsed={collapsed} onCollapse={onCollapse}>
                <div className="logo" />
                <Menu onClick={linkTo} theme="dark" defaultSelectedKeys={['transactions']} mode="inline" selectedKeys={selectedKeys}>

                        <Menu.Item key="transactions" icon={<PieChartOutlined />}>
                            Option 1
                        </Menu.Item>
                    <Menu.Item  key="analytics" icon={<DesktopOutlined />}>
                        Option 2
                    </Menu.Item>
                    <SubMenu key="sub1" icon={<UserOutlined />} title="User">
                        <Menu.Item key="3">Tom</Menu.Item>
                        <Menu.Item key="4">Bill</Menu.Item>
                        <Menu.Item key="5">Alex</Menu.Item>
                    </SubMenu>
                    <SubMenu key="sub2" icon={<TeamOutlined />} title="Team">
                        <Menu.Item key="6">Team 1</Menu.Item>
                        <Menu.Item key="8">Team 2</Menu.Item>
                    </SubMenu>
                    <Menu.Item key="9" icon={<FileOutlined />}>
                        Files
                    </Menu.Item>
                </Menu>
            </Sider>
            <Layout className="site-layout">
                <Header className="site-layout-background" style={{ padding: 0 }} />
                {/* <Content style={{ margin: '0 16px' }}>
                    <Breadcrumb style={{ margin: '16px 0' }}>
                        <Breadcrumb.Item>User</Breadcrumb.Item>
                        <Breadcrumb.Item>Bill</Breadcrumb.Item>
                    </Breadcrumb>
                    <div className="site-layout-background" style={{ padding: 24, minHeight: 360 }}>
                        Bill is a cat.
                    </div>
                </Content> */}
                    <Switch>
                        <Route path ="/transactions">
                            <ListUI/>
                        </Route>
                    </Switch>
                <Footer style={{ textAlign: 'center' }}>Ant Design ©2018 Created by Ant UED</Footer>
            </Layout>
        </Layout>
    </Router>
    );

}

export default LayoutUI;
