import React, { useContext, useState, useEffect, useRef } from 'react';
import { connect } from 'react-redux';
import { Table, Input, Button, Popconfirm, Form, Modal } from 'antd';
import { fetchUserTransactions } from '../actions';
import EditableForm from './EditableForm';


const TableUI = ({fetchUserTransactions, transactions}) => {

  useEffect(() => {
    fetchUserTransactions()
  }, []) 

  const [isModalVisible, setIsModalVisible] = useState(false);
  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  const columns = [
    {
      key:"0",
      title: 'Vendor',
      dataIndex: ['transaction_details', 'vendor'],
      render: text => <a onClick={() => console.log('click')}>
        <div>
        <a onClick={showModal} type="primary"> <h2>{text}</h2></a>
        </div>
        </a>

    },
    {
      key:"1",
      title: 'Total',
      dataIndex: ['transaction_details', 'total'],

    },
    {
      key:"2",
      title: 'Category',
      dataIndex: ['transaction_details', 'category'],
    }
  ];





  return (
    <div>
      <Table 
      onRow={(record, rowIndex) => {
        return {
          onClick: event => {(recoeventrd) => console.log('record ', r)}, // click row
          onDoubleClick: event => {}, // double click row
          onContextMenu: event => {}, // right button click row
          onMouseEnter: event => {}, // mouse enter row
          onMouseLeave: event => {}, // mouse leave row
        };
      }}
      columns={columns}  
      dataSource={transactions} 
      pagination={false} 
      scroll={{ y: 900 }} 
      />
    </div>
  )

}


const mapStateToProps = (state) => {
  return {
    transactions: state.transactions.transactions,
    filter: state.transactions.filter,
    total: state.transactions.total
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    fetchUserTransactions: () => dispatch(fetchUserTransactions())
  }
}
export default connect(mapStateToProps, mapDispatchToProps) (TableUI);
