        allowClear
        defaultValue={defaultValue}

      >
        <Option value="all">All</Option>
        {
          data.map(c => (
            <Option key={c.uuid} value={c.name}>{c.name}</Option>
          ))
        }
      </Select>
        {/* {onCreate ? <Button onClick={() => console.log('click')}>Create</Button> : null} */}
  </div>
)
}

export default SelectUI;
