import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux';
import { fetchUserCategories, createTransactionCategory } from '../actions';
import CreatableSelect from 'react-select/creatable';
import { Button, Select } from 'antd';

const { Option } = Select;

const SelectOrCreate = ({loading, fetchUserCategories, categories }) => {

  const [value, setValue] = useState("")

  useEffect(() => {
    fetchUserCategories()
  }, []) 


  let selectorOptions = categories.map( c => {
    return {
      label:c.name,
      value:c.name
    }
  })


  const onCreate = () => {
    if (value != null) {
      createTransactionCategory(value)
    }
}

const onSubmitTag = (value) => {
  setValue(value.value)
  console.log('VALUE VALUE ', value)
  // fetch(`https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/transactions/${selectedItem.transaction_details.uuid}`, {
  //   method: 'PUT',
  //   headers: {
  //     'Accept': 'application/json',
  //     'Content-Type': 'application/json'
  //   },
  //   body:JSON.stringify({"category":value})
  // })
  // .then(response => response.json())
  // .then(data => { 
  //   reload() 
  //   closeModal()
  //   setValue('')
  //   // setVisible(false)
  //   // reloadModal(value);

  // });
}


return !categories.length 
? <h2>Loading</h2>
:

  <div>
    <CreatableSelect
            onChange={onSubmitTag}
            options={selectorOptions}
            className="select"
            onCreateOption={onCreate}
            isLoading={loading}
            isDisabled={loading}
          />
  </div>

}

const mapStateToProps = (state) => {
  return {
    categories: state.categories.categories,
    loading: state.categories.loading
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    fetchUserCategories: () => dispatch(fetchUserCategories()),
    createTransactionCategory: (category) => dispatch(createTransactionCategory(category)),
  }
}
export default connect(mapStateToProps, mapDispatchToProps) (SelectOrCreate);
