    //       'Accept': 'application/json',
    //       'Content-Type': 'application/json'
    //     },
    //     body:JSON.stringify({"category":newCategory})
    //   })
    //   .then(response => response.json())
    //   .then(data => { setCategories(data) });
    // }
  }

  function onFocus() {

  }


  function onSearch(val) {
    const filteredCategories = data.filter(category => {
      return category.name.includes(val);
    })
    if (filteredCategories.length > 0) {

      // setOnCreate(false)
      // isCreate(false)
    }
    else {
      // setOnCreate(true)
      isCreate(true)
      onType(val)
    }
  }

  function filterOption(input, option) {
    const result = option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0;
    return result
  }



  return (
    <div>
      <Select
        showSearch
        style={{ width: 200 }}
        placeholder="Select a category"
        optionFilterProp="children"
        onChange={onSelect}
        onBlur={onBlur}
        onSearch={onSearch}
        filterOption={(filterOption)}
      >
        <Option value="all">All</Option>
        {
          data.map(c => (
            <Option key={c.uuid} value={c.name}>{c.name}</Option>
          ))
        }
      </Select>
      {/* {onCreate ? <Button onClick={() => console.log('click')}>Create</Button> : null} */}
    </div>
  )
}

export default SelectUI;
