import { Card } from 'antd';
import { Button, Select,  } from 'antd';
import SelectUI from './SelectUI';
import './card.css'


const CardUI = ({onSelect, total}) => {

    const [create, setCreate] = useState(false)

    const isCreate = (value) => {
        setCreate(value)
    }

    const onType = (value) => {
        console.log('create ', value);
    }

    const onCreate = (value) => {
    if(value != null){
      fetch(`https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/categories`, {
        method: 'POST',
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        body:JSON.stringify({"category":value})
      })
      .then(response => response.json())
      .then(data => { console.log(data) });
    }
    }

    const extra = () => {
        return (
            <div className="row">
                <SelectUI onType={onType} isCreate={isCreate} onSelect={onSelect}/>
                {create ? <Button>Create</Button> : null}
            </div>

            )
        }
    return (
        <>
            <Card title="Total" extra={extra()} style={{ width: 500 }}>
                <h1>{total}  â‚ª</h1>
            </Card>
        </>
    );
}

export default CardUI
