import React, { useState, useEffect } from 'react';
import { Card } from 'antd';
import { Button, Select, } from 'antd';
import CreatableSelect from 'react-select/creatable';
import SelectUI from './SelectUI';
import './card.css'


const CardUI = ({ onSelect, total, children, title }) => {


    const [create, setCreate] = useState(false)
    const [newCategory, setNewCategory] = useState(null)
    const [categories, setCategories] = useState([])
    const [reloadTrick, setReloadTrick] = useState("");



    useEffect(() => {
      fetch('https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/categories')
        .then(response => response.json())
        .then(data => { setCategories(data) });

      // console.log(count)
    }, [newCategory]) // if you add count, only run if count changes.



    const selectorOptions = [];

    //selectorOptions.push({
    //    label:"All",
    //    value:'all'
    //  })

    let cat = categories.map( c => {
        return {
          label:c.name,
          value:c.name
        }
      })
    selectorOptions = [{label:"All",value:'all'},  ...cat];


    const isCreate = (value) => {
        setCreate(value)
    }

    const onType = (value) => {
        setNewCategory(value)
    }

    const reload = () => {
        console.log('reload')
        setReloadTrick('lets reload')
      }

    // const onCreate = () => {
    //     if (newCategory != null) {
    //         fetch(`https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/categories`, {
    //             method: 'POST',
    //             headers: {
    //                 'Accept': 'application/json',
    //                 'Content-Type': 'application/json'
    //             },
    //             body: JSON.stringify({ "category": newCategory })
    //         })
    //             .then(response => response.json())
    //             .then(data => { setCategories(data) });
    //     }
    //     console.log('click')
    //     setCreate(false);
    // }

    const extra = () => {
        return !categories.length 
        ? <h1>Loading</h1>
        :
            <div className="row">
                {/* <SelectUI data={categories} onType={onType} isCreate={isCreate} onSelect={onSelect} />
                {create ? <Button onClick={onCreate}>Create</Button> : null} */}
                <CreatableSelect
                  onChange={onSelect}
                  options={selectorOptions}
                  className="select"
                />
            </div>


    }
    return (
        <>
            <Card title={title} extra={extra()} style={{ width: 500 }}>
                {children}
            </Card>
        </>
    );
}

export default CardUI
