import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import Container from '@material-ui/core/Container';
import Scroll from './components/Scroll';
import ListUI from './components/ListUI';
import TableUI from './components/TableUI';
import CardUI from './components/CardUI';


function App() {

  const [transactions, setTransactions] = useState([])
  const [parameter, setParameter] = useState();

  useEffect(() => {
    fetch('https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/transactions')
      .then(response => response.json())
      .then(data => { setTransactions(data.data) });
    // console.log(count)
  }, []) // if you add count, only run if count changes.

  console.log('TRANSAC ', transactions);

  const onSearch = () => {
    setParameter('supermarket')
  }
  return !transactions.length ?
    <h1>Loading</h1> :
    (
      <div className="root">
        <div className="header">
         <h1>TRANSACTIONS</h1>
        </div>
        {/* <Scroll>
          <ListUI data={transactions} />
        </Scroll> */}
        <TableUI data={transactions}/>

        <div className="header">
         <h1>ANALYTICS</h1>
        </div>
        <CardUI onSearch={onSearch}/>
      </div>
    )
}

export default App;
