import React, { useState, useEffect } from 'react';
import { Card } from 'antd';
import { Button, Select, } from 'antd';
import CreatableSelect from 'react-select/creatable';
import SelectCategory from './SelectCategory';
import './card.css'


const CardUI = ({children, title }) => {




  const extra = () => {
    return (
      <div className="row">
        <SelectCategory />
      </div>
    )



  }
  return (
    <>
      <Card title={title} extra={extra()} style={{ width: 500 }}>
        {children}
      </Card>
    </>
  );
}

const mapStateToProps = (state) => {
  return {
    transactions: state.transactions.transactions,
    filter: state.transactions.filter,
    total: state.transactions.total
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    fetchUserTransactions: () => dispatch(fetchUserTransactions()),
    selectTransaction: (transaction) => dispatch(selectTransaction(transaction)),
    openModal: () => dispatch(openModal()),
    setTotal: (total) => dispatch(setTotal(total))
  }
}
export default connect(mapStateToProps, mapDispatchToProps) (CardUI);
