import React, { useContext, useState, useEffect, useRef } from 'react';
import { Table, Input, Button, Popconfirm, Form, Modal } from 'antd';


const TableUI = ({data}) => {

  const [isModalVisible, setIsModalVisible] = useState(false);
  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  const columns = [
    {
      key:"0",
      title: 'Vendor',
      dataIndex: ['transaction_details', 'vendor'],
      render: text => <a onClick={() => console.log('click')}>
        <div>
        <a onClick={showModal} type="primary"> <h2>{text}</h2></a>
        </div>
        </a>

    },
    {
      key:"1",
      title: 'Total',
      dataIndex: ['transaction_details', 'total'],

    },
    {
      key:"2",
      title: 'Category',
      dataIndex: ['transaction_details', 'category'],
    },
    {
        key:"3",
        title: 'Tag',
        dataIndex: ['transaction_details', 'tag'],

    },
  ];

  const components = {
    body: {
      row: EditableRow,
      cell: EditableCell,
    },
  };

  const handleSave = (row) => {
    const newData = [...data];
    const index = newData.findIndex((item) => row.key === item.key);
    const item = newData[index];
    newData.splice(index, 1, { ...item, ...row });
    this.setState({
      dataSource: newData,
    });
  };

  return (
    <div>
      <Modal title="Basic Modal" visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}>

      </Modal>
      <Table columns={finalColumns} components={components} dataSource={data} pagination={false} scroll={{ y: 240 }} />
    </div>
  )

}

export default TableUI;
