import React, {useState, useEffect} from 'react';
import { Modal} from 'antd';
import PopoverUI from './PopoverUI';

const ModalUI = ({reload, visible, handleOk, handleCancel, selectedItem}) => {

    const [reloadTrick, setReloadTrick] = useState("");
    const [category, setCategory] = useState("");


    useEffect(() => {
        console.log('use effetc modal ui')
      }, [category]) 


      const reloadModal = (v) => {
        console.log('reload modal', v)
        setCategory(v)
        console.log('selected ', selectedItem)
        setCategory("")
      }

    return(
        <Modal title={"Transaction details"} visible={visible} onOk={handleOk} onCancel={handleCancel}>
            {selectedItem 
            ? <div className='modal'>
                <div className='row'>
                  <div>Transaction ID : </div>
                  <div>{selectedItem.transaction_details.uuid}</div>
                </div>
                <div className='row'>
                  <div>Vendor: </div>
                  <div>{selectedItem.transaction_details.vendor}</div>
                </div>
                <div className='row'>
                  <div>Category</div>
                  <PopoverUI 
                  closeModal={handleCancel} 
                  selected={selectedItem} 
                  reload={reload}
                  reloadModal={reloadModal}
                  > 
                  {category ? category : selectedItem.transaction_details.category}
                  </PopoverUI>
                </div>
                <div className='row'>
                  <div>Total</div>
                  <div>{selectedItem.transaction_details.total}</div>
                </div>
              </div>
            : <div></div>
            }
          </Modal>
    )
}

export default ModalUI;
