import React, { useState, useEffect } from 'react';
import { Button, Select } from 'antd';

const { Option } = Select;

const SelectUI = ({ onSelect }) => {

  const [categories, setCategories] = useState([])
  const [onCreate, setOnCreate] = useState(false)
  const [newCategory, setNewCategory] = useState(null)

  useEffect(() => {
    fetch('https://2eh1oighrk.execute-api.eu-west-1.amazonaws.com/dev/categories')
      .then(response => response.json())
      .then(data => { setCategories(data) });

    // console.log(count)
  }, []) // if you add count, only run if count changes.

  function onChange(value) {
    console.log(`selected ${value}`);
    onSelect()
  }

  function onBlur() {
    console.log('blur');
  }

  function onFocus() {
    console.log('focus');
  }


  function onSearch(val) {
    const filteredCategories = categories.filter(category =>{
      return category.name.includes(val);
    })
    if(filteredCategories.length > 0){
      console.log('OPTION ', filteredCategories)
      setOnCreate(false)
    }
    else{
      setOnCreate(true)
      setNewCategory(val)
    }
  }
  const onCreateCategory = () => {
    console.log('NEW CATEGORY ', newCategory);
  }

  function filterOption(input, option){
    const result = option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0 ;
    // console.log(option);
    console.log(result);
    console.log(option);

    // console.log(option.children.toLowerCase().indexOf(input.toLowerCase()))
    // if(option.children.toLowerCase().indexOf(input.toLowerCase()) === -1){
    //   console.log('resultt ', result)
    //   setOnCreate(true);
    // }
    // else {
    //   setOnCreate(false);
    // }
    // if(result){
    //   console.log('result ', result)
    //   setOnCreate(false);
    // }
    // else{
    //   console.log('result ', result)
    //   setOnCreate(true);
    // }
    return result
  }

  return !categories.length ?
    <h1>Loading</h1> :
    <div>
      <Select
        showSearch
        style={{ width: 200 }}
        placeholder="Select a category"
        optionFilterProp="children"
        onChange={onSelect}
        onFocus={onFocus}
        onBlur={onBlur}
        onSearch={onSearch}
        filterOption={(filterOption)}
      >
        <Option value="all">All</Option>
        {
          categories.map(c => (
            <Option value={c.name}>{c.name}</Option>
          ))
        }
      </Select>
        <Button onClick={() => console.log('click')}>Create</Button>
    </div>
}

export default SelectUI;
