import React, { useState } from 'react';
import { List, message, Avatar, Spin, Modal} from 'antd';
import InfiniteScroll from 'react-infinite-scroller';
import EditableForm from './EditableForm';
import './list.css'

const fakeDataUrl = 'https://randomuser.me/api/?results=5&inc=name,gender,email,nat&noinfo';

const ListUI = ({data, onSubmitTag}) => {

  const [isModalVisible, setIsModalVisible] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);

  const showModal = () => {
    setIsModalVisible(true);
  };

  const handleOk = () => {
    setIsModalVisible(false);
  };

  const handleCancel = () => {
    setIsModalVisible(false);
  };

  const onTransactionClick = (item) => {
    console.log('ITEM ITEM ' , item)
    setSelectedItem(item);
    showModal()
  }

    return (
      <div className="demo-infinite-container">
        <InfiniteScroll
          initialLoad={false}
          pageStart={0}
          useWindow={false}
          loadMore={() => console.log('load more')}
        >
          <Modal title="Transaction details" visible={isModalVisible} onOk={handleOk} onCancel={handleCancel}>
            <EditableForm onSubmitTag={onSubmitTag}/>
            {selectedItem 
            ? <div>{selectedItem.transaction_details.id}</div>
            : <div></div>
            }
          </Modal>
          <List
            dataSource={data}
            renderItem={item => (
              <div data-tag="test" onClick={() => onTransactionClick(item)}>
                <List.Item data={item} key={item.transaction_details.id}>
                <List.Item.Meta
                  title={<a><h2>{item.transaction_details.vendor}</h2></a>}
                  description={item.transaction_details.category}
                />
                <div><h2>{item.transaction_details.total} â‚ª</h2></div>
              </List.Item>
              </div>
            )}
          >
          </List>
        </InfiniteScroll>
      </div>
    );

}

export default ListUI ;
